function callAlert(){alert("hello")}function calculate(){x=prompt("How much $ was your meal?"),mealTotal=Number(x),document.getElementById("total").innerHTML="$"+mealTotal.toFixed(2);try{if(mealTotal<=0)throw"Make sure you enter a value greater than $0 :)";var e=document.getElementById("0totalTip").innerHTML="$"+mealTotal.toFixed(2),t=.05*mealTotal;document.getElementById("5tip").innerHTML="$"+t.toFixed(2);var l=mealTotal+t;document.getElementById("5totalTip").innerHTML="$"+l.toFixed(2);var a=.1*mealTotal;document.getElementById("10tip").innerHTML="$"+a.toFixed(2);var n=mealTotal+a;document.getElementById("10totalTip").innerHTML="$"+n.toFixed(2);var o=.15*mealTotal;document.getElementById("15tip").innerHTML="$"+o.toFixed(2);var m=mealTotal+o;document.getElementById("15totalTip").innerHTML="$"+m.toFixed(2);var i=.2*mealTotal;document.getElementById("20tip").innerHTML="$"+i.toFixed(2);var d=mealTotal+i;document.getElementById("20totalTip").innerHTML="$"+d.toFixed(2)}catch(e){document.getElementById("total").innerHTML=e}}callAlert();